const Styles = styled.div`
  .domains {
    display: flex;
    justify-content: space-between;
    margin: 0 auto;
    min-width: 550px;
    max-width: 100%;
  }
  .domains div {
    text-align: center;
  }
  .domains span {
    display: block;
    text-align: center;
    color: #221f1f;
    font-size: 20px;
    font-weight: 600;
    text-transform: capitalize;
    margin-top: 10px;
  }
  .domains img {
    margin-bottom: 10px;
    max-width: 135px;
  }

  @media (${Devices.mobile}) {
    .domains {
      flex-direction: row;
      flex-wrap: wrap;
      width: 100%;
      justify-content: center;
      min-width: 0;
    }
    p {
      box-sizing: border-box;
      width: 100%;
      padding: 20px;
    }
    .domains div {
      margin: 20px 0;
      width: 50%;
    }
    .domains img {
      max-width: 100%;
    }
  }
`;

export default Domains;
